app.controller('singleController', ['$scope','movieservice','$routeParams','$http' ,function($scope,movieservice,$routeParams,$http) {
    movieservice.success(function(data){
        $scope.movie = data[$routeParams.id];
        //console.log($scope.movie);
        var checkImg = $scope.movie.poster;
        if(checkImg == "N/A"){
             $scope.movie.poster = "data:image/svg+xml;base64,";
        }
            $scope.editMovie = function(movieId, plot){
                if(confirm("Are you sure to edit this movie?")){
                    $http.post("api/editMovie.php?movieId="+movieId+"&plot="+plot).success(function(data){
                        console.log(data);
                      });
                    }
            };
    }); 
}]);